mapper.select:

select * from (
	     select rownum as rnum, a.* from (
	        		select
	                    *
	                from
	                    board  
	    ) a where rownum <= #{pageNum} * #{amount}
	) where rnum > (#{pageNum}-1) * #{amount} and board_type_number=5


select * from (
	     select rownum as rnum, a.* from (
	        		select
	                    *
	                from
	                    mbr  
	    ) a where rownum <= #{pageNum} * #{amount}
	) where rnum > (#{pageNum}-1) * #{amount} and authority_number=2


select * from (
	     select rownum as rnum, a.* from (
	        		select
	                    *
	                from
	                    prdct  
	    ) a where rownum <= #{cri.pageNum} * #{cri.amount}
	) where rnum > (#{cri.pageNum}-1) * #{cri.amount} and mbr_id=#{m_id}


select * from (
	     select rownum as rnum, a.* from (
	        		select
	                    *
	                from
	                    mbr  
	    ) a where rownum <= #{pageNum} * #{amount}
	) where rnum > (#{pageNum}-1) * #{amount} and authority_number=3
